        require_once('mysql/ext/' . $this->getClazzName($tablename) . 'MySqlExtDAO.class.php');
    }

    public function loadAll($tablenames) {
        foreach ($tablenames as $key => $tablename) {
            $this->load($tablename);
        }
    }

